<template>
  <div v-if="visible">
    <van-popup style="height: 70%;" v-model="visible" position="bottom">
      <!--  弹框-->
      <div class="share-image">
        <div class="share-image-title" @click="visible = false">
          <div class="text">保存到相册</div>
          <div class="close">
            <van-icon name="cross" color="#898989"/>
          </div>
        </div>
        <div class="content">
          <div class="img">
            <img v-if="imgUrl" :style="{width: picWidth}" ref="pic" @load="loadPic" :src="imgUrl" alt="">
            <div v-else class="loading-wrapper">
              <van-loading type="spinner" class="loading"/>
              <span class="text">图片生成中...</span>
            </div>
          </div>
          <button class="keep-btn">长按图片保存到手机</button>
          <div class="tips">保存图片到手机相册后，将图片分享到您的圈子</div>
        </div>
      </div>
    </van-popup>
    <!--  页面-->
    <div class="share-content" ref="shareContent" id="shareContent">
      <!-- <div class="top">{{$store.state.globalConfig.mpName}}</div> -->
      <div class="share-image-content">
        <div class="title">{{proData.showTitle}}</div>
        <div class="price">￥<span class="num">{{proData.activityPrice}}&nbsp;<span class="sale-price" v-if="proData.salePrice > proData.activityPrice">￥{{proData.salePrice}}<p class="line-t"></p></span></span></div>
        <div class="pro-img">
          <img :src="proImgUrl" @load="loadImage" alt="">
        </div>
        <div class="qrcode">
          <div class="img">
            <img :src="qrcode" alt="">
            <!-- <div ref="qrcodeLink" id="qrcodeLink" v-show="$store.state.webtype != 2 || $store.state.webtype != 3"></div> -->
          </div>
          <div class="text">
            <div class="top-title">{{$store.state.globalConfig.mpName}}</div>
            <div class="bottom-tips">长按识别•去逛逛</div>
          </div>
        </div>
        <div class="logo">
          <img :src="baseIcon" alt="">
        </div>
      </div>
    </div>
  </div>
</template>

<script>
  import html2canvas from 'html2canvas'
  import {Loading, Toast} from 'vant';
  import Config from '@/api/config'
  import wxfunc from '@/utils/wxfunc'
  import QRCode from 'qrcodejs2'
  export default {
    name: 'wxShareImage',
    data() {
      return {
        visible: false,
        proData: {},
        imgUrl: '',
        proImgUrl: '',
        qrcode: '',
        picWidth: 0,
        qr:"",
        qrBase64 : "",
        baseIcon:"data:image/png;base64,"
      }
    },
    props: {},
    created(){
    },
    mounted() {
      this.$nextTick(() => {
        this.formatImgUrl(this.proData.phMainUrl)
        // if(this.$store.state.webtype != 2 || this.$store.state.webtype != 3){
        //   this.crateQrcode();
        // }
        console.log('this.proData',this.proData)
      })
    },
    methods: {
      crateQrcode() {
        this.$nextTick(()=>{
          document.getElementById("qrcodeLink").innerHTML = "";
          this.qr = new QRCode("qrcodeLink", {
          width: 100,
          height: 100, // 高度
          text: window.location.href, // 二维码内容
          render: 'canvas', // 设置渲染方式（有两种方式 table和canvas，默认是canvas）
          colorDark : "#000000",
          colorLight : "#ffffff",
        });
        })
      },
      formatImgUrl(url) {
        this.$request.post("/app/json/home/getFileBase64",{filePath:url}).then(res => {
          if (res.status == 0) {
            if (res.data) {
              this.proImgUrl =  `data:image/png;base64,${res.data || ''}`
            } else {
              this.$Toast('getFileBase64 返回为空');
            }
          } else {
            this.$Toast(res.info);
          }
        })
      },
      loadImage() {
        // if(this.$store.state.webtype == 2 || this.$store.state.webtype == 3){
        //   this.getCode().then(() => {
        //     this.canvasPage();
        //   })
        // }else{
        //   this.canvasPage();
        // }
        this.getCode().then(() => {
          this.canvasPage();
        })
      },
      canvasPage(){
        let that = this
        setTimeout(() => {
          let _canvas = document.querySelector('.share-content');
          let w = parseInt(window.getComputedStyle(_canvas).width);
          let h = parseInt(window.getComputedStyle(_canvas).height);
          let canvas = document.createElement("canvas");
          let scale = 1;
          canvas.width = w * scale;
          canvas.height = h * scale;
          canvas.getContext("2d").scale(scale, scale);
          html2canvas(that.$refs.shareContent, {
            useCORS: true,
            // scale: 1,
            canvas: canvas
          }).then(canvas => {
            that.imgUrl = canvas.toDataURL()
          })
        }, 20)
      },
      saveImage() {
        const base64DArr = this.imgUrl.split(',')[1]
        let that = this
        this.$request.post('/app/json/we_chat/uploadTemporaryWxFile', {
          mediaType: 'image',
          mediaBase64: base64DArr,
          fileName: '1',
          fileType: 'png'
        }).then(res => {
          if (res.status === 0) {
            // Toast("上传成功！");
            wxfunc.getWechatSignature((isSignature) => {
              //签名成功
              if (isSignature) {
                wx.downloadImage({
                  serverId: res.data.mediaId, // 需要下载的图片的服务器端ID，由uploadImage接口获得
                  isShowProgressTips: 1, // 默认为1，显示进度提示
                  success: function (res) {
                    Toast("保存成功！");
                  }
                })
              } else {
                Toast('签名失败');
              }
            })
          } else {
            Toast("保存失败！");
          }
        }).catch(err => {
          Toast(err || '上传失败');
        })
        // this.$bridgefunc.savePhoto(base64DArr, result => {
        //   if (result == "1") {
        //     this.visible = false;
        //     this.$Toast("保存成功！");
        //   } else {
        //     this.$Toast("保存失败！");
        //   }
        // });
      },
      getCode: function () {
        return new Promise((resolve, reject) => {
          // let rfrCode = this.$store.state.rfrCode || this.$store.state.userInfo.userId
          this.$request.post('/app/json/short_address/makeShortAddress', {
            // longAddress: `${this.delParams()}&rfrCode=${rfrCode}`
            longAddress: `${encodeURIComponent(`/app-vue/app/index.html#/mall2/detail/1000?skuId=${this.proData.skuId}`)}`
          }).then(res => {
            if (res.status === 0) {
              const shortCode = res.data.substr(res.data.indexOf('/s/'))
              // /app/json/we_chat/getwxacodeunlimit
              console.log('shortCode',shortCode)
              this.$request.post('/app/json/wechat_mini_program/createQrCode', {
                // path: `pages/common/home/index`,
                // scene: shortCode,
                // width: 430
                page: `pages/common/home/index`,
                scene: shortCode,
                width: 430,
                type: "0",
              }).then(res => {
                if (res.status === 0) {
                  this.qrcode = 'data:image/png;base64,' + res.data
                  const img = new Image()
                  img.src = this.qrcode
                  img.onload = () => {
                    resolve()
                  }
                } else {
                  reject()
                  this.$Toast(res.info || '获取小程序码失败')
                }
              }, error => {
              })
            }
          })
        })
      },
      delParams() {
        let _url = window.location.href
        if (_url.indexOf('shareDeliveryType') == -1 && this.proData.deliveryType) {
          _url = _url + '&shareDeliveryType=' + this.proData.deliveryType;
        }
        const ind = _url.lastIndexOf('?')
        const path = _url.substr(0, ind)
        _url = _url.substr(_url.lastIndexOf('?'))
        let GETs = _url.slice(1).split('&')
        let parmas = ''
        for (let i = 0; i < GETs.length; i++) {
          let tmpArr = GETs[i].split('=')
          let key = tmpArr[0]
          if (!(key === 'token' || key === 'userInfo')) {
            parmas += `&${GETs[i]}`
          }
        }
       return `${path}?${parmas.substr(1)}`
      },
      loadPic() {
        this.picWidth = this.$refs.pic.clientHeight / this.$refs.pic.naturalHeight * this.$refs.pic.naturalWidth + 'px'
      }
    },
    components: {
      [Loading.name]: Loading
    }
  }
</script>

